mapper.select:
        
			SELECT distinct v.rwid, v.sjhm, v.dxnr, v.wxzhid, v.qyhid, v.wxyyid, v.title, v.url,v.fsnrlx FROM db_zgfz.v_dx_fs_md v
			WHERE v.fsfs = #{fsfs}
		
        
            AND v.tmid = #{tmid}
        
        
            AND v.tmid > 0
        
        
			ORDER BY v.rwid

		
            SELECT t.sqid AS vipid
              FROM db_zgfz.t_wxqy_dzb t
            WHERE t.wxzhid = #{0}
                AND t.djxh = #{1}
                AND t.yxbz = 'Y'
            ORDER BY t.bsylx

		

			SELECT count(1)
			  FROM db_zgfz.t_wxqy_dzb t, db_wx.t_wx_nsrmd n
			WHERE t.djxh = n.djxh
				AND t.wxzhid = #{0}
				AND t.djxh = #{1}
				AND t.yxbz = 'Y'

mapper.update:
    	
   			update db_zgfz.t_fsmd t set t.fssx = #{fssx} , t.fsz =#{msgGroup, jdbcType=VARCHAR}  
   			where t.rwid = #{rwid} and t.sjhm IN
   		
		
		    #{sjhmList}

mapper.select:
        
			SELECT WYID, ZCBH, XXHFL, BQLX, ZCBH_YS, BMMC, SYR, ZCXL_DM, ZCXLMC, ZCMC, PP, GGXH, JGZB, CFCK, JQBH, RKSJ, ZT, WL, IP, WLBH, WZM, SYYT, SYQK, DWMC, DWBH, RKDJ, BZ1, BZ2, WZ_BZ, SYZT
			FROM db_wx.T_SBGL_ZCRK_XLB
			WHERE  WYID = #{wyid} or ZCBH= #{wyid}
