mapper.select:
        
			SELECT distinct v.rwid, v.sjhm, v.dxnr, v.wxzhid, v.qyhid, v.wxyyid FROM db_zgfz.v_dx_fs_md v
			WHERE v.fsfs = #{fsfs}
		
        
            AND v.tmid = #{tmid}
        
        
            AND v.tmid > 0
        
        
			ORDER BY v.rwid

		
      		SELECT t.sqid || SUBSTR(TO_CHAR(TO_NUMBER(SUBSTR(T.SJHM, -3)) +
                            TO_NUMBER(SUBSTR(TO_CHAR(SYSDATE, 'HH24MI'),0,3))), -3) AS vipid
			  FROM db_zgfz.t_wxqy_dzb t
			WHERE t.wxzhid = #{0}
			   	AND t.djxh = #{1}
			   	AND t.yxbz = 'Y'

		

			SELECT count(1)
			  FROM db_zgfz.t_wxqy_dzb t, db_wx.t_wx_nsrmd n
			WHERE t.djxh = n.djxh
				AND t.wxzhid = #{0}
				AND t.djxh = #{1}
				AND t.yxbz = 'Y'

mapper.update:
    	
   			update db_zgfz.t_fsmd t set t.fssx = #{fssx} , t.fsz =#{msgGroup, jdbcType=VARCHAR}  
   			where t.rwid = #{rwid} and t.sjhm IN
   		
		
		    #{sjhmList}
